<?php

/*
	@abstract Generate ShareThis by Facebook, Twitter, Pinterest, Google+ and eMail Button
	@example  html_sharethis('http://example.com','Example Text #PHP')
	@param    string $url [with http://]
	@param    string $text [text with #hashtags]
	@param    string $hint [linktext default="Please share all the Love of this Page now"]
	@return   string [html]
	@link     https://www.base64-image.de/
	@todo     alternate images
	@version  2.0
*/

if(basename(__FILE__)==basename($_SERVER['PHP_SELF']))echo html_sharethis('http://example.com','Example Text #PHP');

function html_sharethis($url,$text='',$hint='Please share all the Love of this Page now'){
  preg_match_all("~#(\w+)~", $text, $matches);
  $hashtags = implode(',', $matches[1]);
  $text = str_replace('#','',$text);
  $css = '<style type="text/css">#stp img{width:36px;padding:6px 5px 6px 0;border:0;box-shadow:none !important;display:inline;vertical-align:middle;}#stp,#stp a{font-family:Helvetica,Arial;font-size:11px;}</style>';
  $f_icon = 'data:image/png;base64,';
  $t_icon = 'data:image/png;base64,';
  $p_icon = 'data:image/png;base64,';
  $g_icon = 'data:image/png;base64,';
  $e_icon = 'data:image/png;base64,';
  $return = $css.'<div id="stp"><p>'.$hint.'</p><a 
  href="http://www.facebook.com/sharer.php?u='.$url.'" target="_blank"><img 
  title="Facebook" class="stp" alt="Facebook" src="'.$f_icon.'" /></a><a 
  href="http://twitter.com/share?url='.$url.'&text='.$text.'&hashtags='.urlencode($hashtags).'" target="_blank"><img 
  title="Twitter" class="stp" alt="Twitter" src="'.$t_icon.'" /></a><a 
  href="javascript:void((function()%7Bvar%20e=document.createElement(&apos;script&apos;);e.setAttribute(&apos;type&apos;,&apos;text/javascript&apos;);e.setAttribute(&apos;charset&apos;,&apos;UTF-8&apos;);e.setAttribute(&apos;src&apos;,&apos;http://assets.pinterest.com/js/pinmarklet.js?r=&apos;+Math.random()*99999999);document.body.appendChild(e)%7D)());"><img 
  title="Pinterest" class="stp" alt="Pinterest" src="'.$p_icon.'" /></a><a 
  href="https://plus.google.com/share?url='.$url.'" target="_blank"><img 
  title="Google+" class="stp" alt="Google+" src="'.$g_icon.'" /></a><a 
  href="mailto:?Subject='.($text==''?$url:$text).'&Body='.$url.'"><img 
  title="eMail" class="stp" alt="eMail" src="'.$e_icon.'" /></a></div>';
  return $return;
}
